import '../styles/App.scss';
import { useEffect, useState } from 'react';
import callToApi from '../services/api';
import Filters from './Filters';
import CharacterList from './CharacterList';

function App() {
  ////Variables de estado////
  const [character, setCharacter] = useState([]);
  const [searchName, setSearchName] = useState('');

  useEffect(() => {
    callToApi().then((resp) => {
      setCharacter(resp);
      console.log(resp);
    });
  }, []);

  const handleChangeSearchName = (ev) => {
    setSearchName(ev.currentTarget.value);
  };

  const filteredCharacter = character.filter((contact) =>
    contact.name.toLowerCase().includes(searchName.toLocaleLowerCase)
  );

  return (
    <>
      <div>
        <header className="header">
          <img
            className="header_image"
            src="https://help.redbubble.com/hc/article_attachments/360002309526/Rick_and_Morty_-_logo__English_.png"
            alt="img"
          />
        </header>
        <section>
          <CharacterList
            searchName={searchName}
            handleChangeSearchName={handleChangeSearchName}
          />
        </section>
        <section>
          <Filters character={filteredCharacter} />
        </section>
        <ul>{renderCharacter()}</ul>
      </div>
    </>
  );
}

export default App;
