import '../styles/App.scss';
import { useEffect, useState } from 'react';
import callToApi from '../services/api';
import Filters from './Filters';
import CharacterList from './CharacterList';
import CharacterDetail from './CharacterDetail';

function App() {
  ////Variables de estado////
  const [character, setCharacter] = useState([]);
  const [searchName, setSearchName] = useState('');

  useEffect(() => {
    callToApi().then((resp) => {
      setCharacter(resp);
      console.log(resp);
    });
  }, []);

  const filteredCharacter = character.filter((contact) =>
    contact.name.toLocaleLowerCase().includes(searchName.toLocaleLowerCase())
  );

  const renderCharacter = () => {
    return character.map((character, i) => {
      return (
        <li key={i}>
          <img src={character.img} alt="img" className="img" />
          <h4>{character.name}</h4>
          <p>{character.species}</p>
        </li>
      );
    });
  };

  return (
    <>
      <div>
        <header className="header">
          <img
            className="header_image"
            src="https://help.redbubble.com/hc/article_attachments/360002309526/Rick_and_Morty_-_logo__English_.png"
            alt="img"
          />
        </header>
        <section>
          <form action="">
            <input type="text" name="name" id="name" />
          </form>
        </section>
        <section>
          <CharacterDetail character={filteredCharacter} />
        </section>
        <ul>{renderCharacter()}</ul>
      </div>
    </>
  );
}

export default App;
